example.code:
private func Fusing()
{
	// Countdown (~10 seconds)
	Message("@%d",(370-GetActTime())/37);
	// if GetActTime is a multiple of 37 (=every 37 frames do...)
	if(!GetActTime()%37)
	{
		// Sound "tick" if the count is even, "tack" if it is odd 
		if(!(GetActTime()/37%2)) Sound("Tick");
		else Sound("Tack");
	}
	// Explosion
	if(GetActTime() > 370)
		Explode(80);
}
