Log(" A tools workshop requires the following material:");

for(var i = 0;; i++)
{
  var id = GetDefCoreVal("Components", "DefCore", ToolsWorkshop, i*2);
  if(!id) break;
  var cnt = GetDefCoreVal("Components", "DefCore", ToolsWorkshop, i*2+1);

  Log("%d x %s", cnt, id->GetName());
}
