func Initialize() {
  AddEffect("VariateHue",this,200,1,this);
  return _inherited();
}

func FxVariateHueTimer() {
  var hsl = RGB2HSL(GetColor());
  var hsla_array = SplitRGBaValue(hsl);
  hsla_array[0] += 2;
  if(hsla_array[0] > 255) hue = 0;
  var rgb = HSL2RGB(RGB(hsla_array[0],hsla_array[1],hsla_array[2]));
  SetColor(rgb);
}
